import './App.css';
import { useEffect, useState, useCallback } from "react";
import { ethers } from "ethers";
import TokenArtifact from "./artifacts/contracts/TuringToken.sol/TuringToken.json";

const contractAddress = "0x5fbdb2315678afecb367f032d93f642f64180aa3"; // Endereço do contrato
const contractABI = TokenArtifact.abi;

export default function TuringDapp() {
    const [contract, setContract] = useState(null);
    const [codename, setCodename] = useState("");
    const [amount, setAmount] = useState("");
    const [ranking, setRanking] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        if (window.ethereum) {
            const web3Provider = new ethers.providers.Web3Provider(window.ethereum);
            const signer = web3Provider.getSigner();
            setContract(new ethers.Contract(contractAddress, contractABI, signer));
        } else {
            console.error("Ethereum wallet não detectada. Instale o MetaMask.");
        }
    }, []);

    const updateRanking = useCallback(async () => {
        if (contract) {
            try {
                // Substituir getAllUsers por uma outra abordagem, ou adicionar a função no contrato
                const users = await contract.getAllUsers(); // Certifique-se de que essa função existe no contrato!
                const balances = await Promise.all(users.map(user => contract.balanceOf(user)));

                const ranked = users.map((user, i) => ({
                    user,
                    balance: balances[i]
                })).sort((a, b) => b.balance - a.balance);

                setRanking(ranked);
            } catch (error) {
                console.error("Erro ao atualizar ranking:", error);
            }
        }
    }, [contract]);

    useEffect(() => {
        if (contract) {
            const handleVoteCast = (voter, amount) => {
                console.log(`Voto lançado por ${voter}: ${amount} Turings`);
                updateRanking();
            };

            contract.off("VotoEmitido", handleVoteCast); // Remover listener antigo
            contract.on("VotoEmitido", handleVoteCast);

            return () => contract.off("VotoEmitido", handleVoteCast);
        }
    }, [contract, updateRanking]);

    const issueToken = async () => {
        if (contract) {
            try {
                setLoading(true);
                const tx = await contract.issueToken(codename, ethers.utils.parseUnits(amount, 18));
                await tx.wait();
                alert("Token emitido com sucesso!");
            } catch (error) {
                console.error("Erro ao emitir token:", error);
                alert("Falha ao emitir token. Verifique seu saldo e tente novamente.");
            } finally {
                setLoading(false);
            }
        }
    };

    const vote = async () => {
        if (contract) {
            try {
                setLoading(true);
                const tx = await contract.vote(codename, ethers.utils.parseUnits(amount, 18));
                await tx.wait();
                alert("Voto registrado com sucesso!");
            } catch (error) {
                console.error("Erro ao votar:", error);
                alert("Falha ao votar. Verifique seu saldo e tente novamente.");
            } finally {
                setLoading(false);
            }
        }
    };

    const votingOn = async () => {
        if (contract) {
            try {
                const tx = await contract.votingOn();
                await tx.wait();
                alert("Votação ativada!");
            } catch (error) {
                console.error("Erro ao ativar votação:", error);
            }
        }
    };

    const votingOff = async () => {
        if (contract) {
            try {
                const tx = await contract.votingOff();
                await tx.wait();
                alert("Votação desativada!");
            } catch (error) {
                console.error("Erro ao desativar votação:", error);
            }
        }
    };

    return (
        <div className="p-6 max-w-3xl mx-auto">
            <h2 className="text-2xl font-bold mb-4">Turing Token DApp</h2>

            <div className="mb-4">
                <input
                    type="text"
                    placeholder="Codename"
                    value={codename}
                    onChange={(e) => setCodename(e.target.value)}
                    className="p-2 border rounded w-full mb-2"
                />
                <input
                    type="text"
                    placeholder="Quantidade"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    className="p-2 border rounded w-full"
                />
            </div>

            <div className="flex flex-wrap gap-4">
                <button onClick={issueToken} className="px-4 py-2 bg-blue-500 text-white rounded" disabled={loading}>
                    {loading ? "Emitindo..." : "Emitir Token"}
                </button>
                <button onClick={vote} className="px-4 py-2 bg-green-500 text-white rounded" disabled={loading}>
                    {loading ? "Votando..." : "Votar"}
                </button>
                <button onClick={votingOn} className="px-4 py-2 bg-yellow-500 text-white rounded">
                    Ativar Votação
                </button>
                <button onClick={votingOff} className="px-4 py-2 bg-red-500 text-white rounded">
                    Desativar Votação
                </button>
            </div>

            <div className="mt-6">
                <h3 className="text-lg font-semibold">Ranking</h3>
                <ul>
                    {ranking.map((entry, index) => (
                        <li key={index}>
                            {entry.user}: {ethers.utils.formatUnits(entry.balance, 18)} Turings
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}



