import './App.css';

import { useEffect, useState } from "react";
import { ethers } from "ethers";
import TokenArtifact from "./artifacts/contracts/TuringToken.sol/TuringToken.json"

const contractAddress = "0x5fbdb2315678afecb367f032d93f642f64180aa3"; // Endereço do contrato
const contractABI = TokenArtifact.abi;

export default function TuringDapp() {
    const [provider, setProvider] = useState(null);
    const [contract, setContract] = useState(null);
    const [signer, setSigner] = useState(null);
    const [codename, setCodename] = useState("");
    const [amount, setAmount] = useState("");
    const [ranking, setRanking] = useState([]);

    useEffect(() => {
        if (window.ethereum) {
            const web3Provider = new ethers.providers.Web3Provider(window.ethereum);
            setProvider(web3Provider);
            setSigner(web3Provider.getSigner());
            setContract(new ethers.Contract(contractAddress, contractABI, web3Provider.getSigner()));
        }
    }, []);

    useEffect(() => {
        if (contract) {
            contract.on("VoteCast", (voter, amount) => {
                console.log(`Vote lançado por ${voter}: ${amount} Turings`);
                updateRanking();
            });
        }
    }, [contract]);

    const issueToken = async () => {
        if (contract) {
            const tx = await contract.issueToken(codename, ethers.utils.parseUnits(amount, 18));
            await tx.wait();
        }
    };

    const vote = async () => {
        if (contract) {
            const tx = await contract.vote(codename, ethers.utils.parseUnits(amount, 18));
            await tx.wait();
        }
    };

    const votingOn = async () => {
        if (contract) {
            const tx = await contract.votingOn();
            await tx.wait();
        }
    };

    const votingOff = async () => {
        if (contract) {
            const tx = await contract.votingOff();
            await tx.wait();
        }
    };

    const updateRanking = async () => {
        if (contract) {
            // Simulação de pooling
            const users = [/* Lista de usuários */];
            const balances = await Promise.all(users.map(user => contract.balanceOf(user)));
            const ranked = users.map((user, i) => ({ user, balance: balances[i] }))
                .sort((a, b) => b.balance - a.balance);
            setRanking(ranked);
        }
    };

    return (
        <div className="p-6 max-w-3xl mx-auto">
            <div className="mt-6">
                <h3 className="text-lg font-semibold">Ranking</h3>
                <ul>
                    {ranking.map((entry, index) => (
                        <li key={index}>{entry.user}: {ethers.utils.formatUnits(entry.balance, 18)} Turings</li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

